mapper.select:
SELECT ap.product_id,ap.product_name, ap.product_type, ap.product_code, ap.product_manager,ap.product_status, ap.create_time, uu.realname, apd.product_net_value,apd.net_asset_value,apd.product_shares,apd.stock_total_assets, apd.security_total_value,apd.stock_total_value,apd.short_total_value FROM ams_product ap LEFT JOIN ams_product_detail apd ON ap.product_id = apd.product_id LEFT JOIN upms_user uu ON ap.operator_id = uu.user_id
WHERE ap.product_name LIKE %#{search}%
ORDER BY ap.product_id ASC LIMIT #{offset},#{limit}
